const data = $input.first().json;
const timestamp = new Date().toISOString().replace(/[:.]/g, '-');

// Generate SQL dump format
let sqlDump = `-- Database Backup: planning\n-- Generated: ${data.backup_timestamp}\n\n`;

// Restaurants
if (data.restaurants && data.restaurants.length > 0) {
  sqlDump += '-- RESTAURANTS\n';
  for (const r of data.restaurants) {
    sqlDump += `INSERT INTO restaurants (id, name, location, address, opening_hours, closing_hours, created_at) VALUES (${r.id}, '${r.name}', '${r.location}', ${r.address ? "'" + r.address + "'" : 'NULL'}, '${r.opening_hours || '10:00'}', '${r.closing_hours || '23:00'}', '${r.created_at}');\n`;
  }
  sqlDump += '\n';
}

// Employees
if (data.employees && data.employees.length > 0) {
  sqlDump += '-- EMPLOYEES\n';
  for (const e of data.employees) {
    const positions = e.positions ? `ARRAY[${e.positions.map(p => "'" + p + "'").join(',')}]::TEXT[]` : 'NULL';
    sqlDump += `INSERT INTO employees (id, last_name, first_name, phone, email, restaurant_id, is_mobile, positions, active, hire_date, created_at) VALUES (${e.id}, '${e.last_name}', ${e.first_name ? "'" + e.first_name + "'" : 'NULL'}, ${e.phone ? "'" + e.phone + "'" : 'NULL'}, ${e.email ? "'" + e.email + "'" : 'NULL'}, ${e.restaurant_id || 'NULL'}, ${e.is_mobile}, ${positions}, ${e.active}, ${e.hire_date ? "'" + e.hire_date + "'" : 'NULL'}, '${e.created_at}');\n`;
  }
  sqlDump += '\n';
}

// Shifts
if (data.shifts && data.shifts.length > 0) {
  sqlDump += '-- SHIFTS\n';
  for (const s of data.shifts) {
    sqlDump += `INSERT INTO shifts (id, employee_id, restaurant_id, date, start_time, end_time, break_start, break_duration, position, is_mission, status, notes, created_at) VALUES (${s.id}, ${s.employee_id}, ${s.restaurant_id}, '${s.date}', '${s.start_time}', '${s.end_time}', ${s.break_start ? "'" + s.break_start + "'" : 'NULL'}, ${s.break_duration || 30}, ${s.position ? "'" + s.position + "'" : 'NULL'}, ${s.is_mission}, '${s.status}', ${s.notes ? "'" + s.notes.replace(/'/g, "''") + "'" : 'NULL'}, '${s.created_at}');\n`;
  }
  sqlDump += '\n';
}

// Absences
if (data.absences && data.absences.length > 0) {
  sqlDump += '-- ABSENCES\n';
  for (const a of data.absences) {
    sqlDump += `INSERT INTO absences (id, employee_id, type, start_date, end_date, status, comment, approved_by, approval_date, created_at) VALUES (${a.id}, ${a.employee_id}, '${a.type}', '${a.start_date}', '${a.end_date}', '${a.status}', ${a.comment ? "'" + a.comment.replace(/'/g, "''") + "'" : 'NULL'}, ${a.approved_by ? "'" + a.approved_by + "'" : 'NULL'}, ${a.approval_date ? "'" + a.approval_date + "'" : 'NULL'}, '${a.created_at}');\n`;
  }
  sqlDump += '\n';
}

return {
  success: true,
  filename: `planning_backup_${timestamp}.sql`,
  content: sqlDump,
  timestamp: data.backup_timestamp,
  tables: {
    restaurants: data.restaurants?.length || 0,
    employees: data.employees?.length || 0,
    shifts: data.shifts?.length || 0,
    absences: data.absences?.length || 0,
    missions: data.missions?.length || 0
  }
};

