const rows = $input.all().map(item => item.json);

if (rows.length === 0) {
  return {
    success: true,
    message: 'No employees to export',
    csv: 'id,last_name,first_name,phone,email,restaurant,is_mobile,positions,active,hire_date,created_at',
    count: 0
  };
}

const headers = ['id', 'last_name', 'first_name', 'phone', 'email', 'restaurant', 'is_mobile', 'positions', 'active', 'hire_date', 'created_at'];

const escapeCSV = (value) => {
  if (value === null || value === undefined) return '';
  const str = String(value);
  if (str.includes(',') || str.includes('"') || str.includes('\n')) {
    return '"' + str.replace(/"/g, '""') + '"';
  }
  return str;
};

let csv = headers.join(',') + '\n';
for (const row of rows) {
  const values = headers.map(h => escapeCSV(row[h]));
  csv += values.join(',') + '\n';
}

return {
  success: true,
  csv: csv,
  count: rows.length,
  filename: `employees_export_${new Date().toISOString().split('T')[0]}.csv`
};

