const response = $input.first().json;
const originalQuestion = $('Prepare Prompt').first().json.question;
let aiContent = response?.message?.content || response?.choices?.[0]?.message?.content || response?.content || 'Sorry, I could not generate a response.';

// Parse any action commands from the response
let action = null;
const actionMatch = aiContent.match(/```action\s*([\s\S]*?)```/);
if (actionMatch) {
  try {
    action = JSON.parse(actionMatch[1].trim());
  } catch (e) {
    // Invalid JSON, ignore
  }
}

return {
  success: true,
  question: originalQuestion,
  response: aiContent,
  action: action,
  model: response?.model || 'llama-3.3-70b',
  timestamp: new Date().toISOString()
};
