// Algorithmic Schedule Generator with morning/afternoon shifts
const webhookData = $('Webhook Trigger').first().json;
const allEmployees = $('Fetch Employees').all().map(e => e.json);
const allLeaves = $('Fetch Leaves').all().map(l => l.json);

const startDate = new Date(webhookData.body?.start_date || new Date().toISOString().split('T')[0]);
const endDate = new Date(webhookData.body?.end_date || new Date(Date.now() + 7*24*60*60*1000).toISOString().split('T')[0]);
const createShifts = webhookData.body?.create_shifts === true;
const includeExtras = webhookData.body?.include_extras === true;

// Two shifts per day: morning and afternoon
const SHIFTS = [
  { name: 'morning', start: '10:00', end: '18:00', break_start: '14:00', hours: 8 },
  { name: 'afternoon', start: '15:00', end: '23:00', break_start: '19:00', hours: 8 }
];

// Track hours worked per employee this week
const hoursWorked = {};
allEmployees.forEach(e => { hoursWorked[e.id] = 0; });

// Track who worked which shift today to avoid double-booking
const dailyAssignments = {};

// Helper: check if employee is on leave
function isOnLeave(empId, date) {
  const d = date.toISOString().split('T')[0];
  return allLeaves.some(l => 
    l.employee_id === empId && 
    d >= l.start_date && d <= l.end_date
  );
}

// Helper: check if it's employee's fixed day off (0=Mon, 6=Sun)
function isFixedDayOff(emp, date) {
  if (emp.fixed_day_off === null || emp.fixed_day_off === undefined) return false;
  const dayOfWeek = (date.getDay() + 6) % 7;
  return emp.fixed_day_off === dayOfWeek;
}

// Helper: check if part-time employee would exceed their hours
function wouldExceedHours(emp, hoursToAdd) {
  if (emp.employment_type !== 'part_time' || !emp.max_hours_per_week) return false;
  return (hoursWorked[emp.id] + hoursToAdd) > emp.max_hours_per_week;
}

// Helper: check if employee already assigned today
function alreadyAssignedToday(empId, dateStr) {
  return dailyAssignments[dateStr]?.includes(empId);
}

const shifts = [];
const planning = [];
const alerts = [];
let currentDate = new Date(startDate);

while (currentDate <= endDate) {
  const dateStr = currentDate.toISOString().split('T')[0];
  dailyAssignments[dateStr] = [];

  [1, 2].forEach(restaurantId => {
    const restaurantName = restaurantId === 1 ? 'A la mer by Kosmo' : 'Kosmo';
    const dayShifts = [];

    // Get available employees for this restaurant
    const available = allEmployees.filter(e => {
      if (e.restaurant_id !== restaurantId && !e.is_mobile) return false;
      if (isOnLeave(e.id, currentDate)) return false;
      if (isFixedDayOff(e, currentDate)) return false;
      if (e.employment_type === 'extra' && !includeExtras) return false;
      return true;
    });

    // Sort: full-time first, then part-time, then extras
    available.sort((a, b) => {
      const order = { full_time: 0, part_time: 1, extra: 2 };
      return (order[a.employment_type] || 0) - (order[b.employment_type] || 0);
    });

    const dayIndex = Math.floor((currentDate - startDate) / (1000*60*60*24));

    // Assign to each shift (morning and afternoon)
    SHIFTS.forEach((shiftType, shiftIdx) => {
      let shiftCount = 0;
      const minPerShift = 3;

      // Rotate employees between morning/afternoon based on day
      const rotatedEmployees = [...available];
      if ((dayIndex + shiftIdx) % 2 === 1) {
        rotatedEmployees.reverse();
      }

      rotatedEmployees.forEach((emp, idx) => {
        // Skip if already assigned today (one shift per day per employee)
        if (alreadyAssignedToday(emp.id, dateStr)) return;

        // Skip if we have enough for this shift
        if (shiftCount >= 5) return;

        // Check part-time hours limit
        if (wouldExceedHours(emp, shiftType.hours)) return;

        // Alternate assignment between shifts based on index + day
        if (shiftCount >= minPerShift && (idx + dayIndex) % 2 !== shiftIdx) return;

        const positions = emp.positions || ['service'];
        const position = positions[(dayIndex + idx) % positions.length];
        const isMission = emp.restaurant_id !== restaurantId;

        const shift = {
          employee_id: emp.id,
          employee_name: `${emp.first_name} ${emp.last_name}`,
          restaurant_id: restaurantId,
          date: dateStr,
          start_time: shiftType.start,
          end_time: shiftType.end,
          break_start: shiftType.break_start,
          break_duration: 30,
          position: position,
          is_mission: isMission,
          status: 'scheduled',
          notes: `${shiftType.name}${emp.employment_type !== 'full_time' ? ' (' + emp.employment_type.replace('_', '-') + ')' : ''}`
        };

        shifts.push(shift);
        dayShifts.push(shift);
        hoursWorked[emp.id] += shiftType.hours;
        dailyAssignments[dateStr].push(emp.id);
        shiftCount++;
      });

      if (shiftCount < minPerShift) {
        alerts.push(`${dateStr} ${restaurantName} ${shiftType.name}: Only ${shiftCount} employees (min ${minPerShift})`);
      }
    });

    planning.push({
      date: dateStr,
      restaurant: restaurantName,
      shifts: dayShifts
    });
  });

  currentDate.setDate(currentDate.getDate() + 1);
}

const suggestions = ['Schedule uses morning (10:00-18:00) and afternoon (15:00-23:00) shifts'];
if (!includeExtras) suggestions.push('Extras not included - set include_extras=true to add them');

return {
  success: true,
  create_shifts: createShifts,
  period: {
    start: startDate.toISOString().split('T')[0],
    end: endDate.toISOString().split('T')[0]
  },
  planning: planning,
  shifts: shifts,
  total_shifts: shifts.length,
  alerts: alerts,
  suggestions: suggestions
};

const data = $('Generate Schedule').first().json;
const inserted = $('Insert Shifts').all();
return {
  success: true,
  message: `Created ${inserted.length} shifts`,
  period: data.period,
  shifts_created: inserted.length,
  planning: data.planning
};
